import React, { useEffect, useRef } from 'react'

function submitImage() {

    let videoRef = useRef(null)
    let photoRef = useRef(null)


    const getUserCamera = () => {
        navigator.mediaDevices.getUserMedia({
            video:true
        })
    }

    useEffect(() => {
        getUserCamera()
    },[videoRef])

  return (
    <div>submitImage</div>
  )
}

export default submitImage;
