import React, { useEffect, useRef } from 'react'

export const SubmitImage = () => {

    let videoRef = useRef(null)
    let photoRef = useRef(null)


    const getUserCamera = () => {
        navigator.mediaDevices.getUserMedia({
            video:true
        })
        .then((stream) => {
          let video = videoRef.current
          video.srcObject = stream
          video.play()
        })
        .catch((error) => {})
    }

    useEffect(() => {
        getUserCamera()
    },[videoRef])

  return (
    <div>
    SubmitImage</div>
  )
}
